mapper.select:
		select `id`,status,`name`,jiangguo_flag,`create_time`,`create_by`,`update_time`,`update_by`,`remarks` from product_category where deleteEnable=0

		  		   and id = #{id} 
		  		   and name = #{name} 
		  		   and jiangguo_flag = #{jiangguoFlag} 
		  		   and status = #{status} 
		  		   and create_time = #{createTime} 
		  		   and create_by = #{createBy} 
		  		   and update_time = #{updateTime} 
		  		   and update_by = #{updateBy} 
		  		   and remarks = #{remarks} 



                order by ${sort} ${order}


                order by id desc



			limit #{offset}, #{limit}

select count(*) from product_category where deleteEnable=0

