mapper.select:
		select goods.id,goods.spec,goods.total,goods.surplus,goods.name,product_product.name productName,goods.goods_number,goods.tgoods_number,goods.product_id,goods.status,goods.discount_price,goods.pay_price,goods.vip_price,goods.promotion_price,goods.create_time,goods.create_by,goods.update_time,goods.update_by,goods.remarks from goods,product_product
        where goods.product_id=product_product.id and goods.deleteEnable=0

		  		   and goods.name = #{name} 
		  		   and goods.goods_number = #{goodsNumber} 
		  		   and goods.tgoods_number = #{tgoodsNumber} 
		  		   and goods.product_id = #{productId} 
		  		   and goods.status = #{status} 
		  		   and goods.discount_price = #{discountPrice} 
		  		   and goods.pay_price = #{payPrice} 
		  		   and goods.vip_price = #{vipPrice} 
		  		   and goods.promotion_price = #{promotionPrice} 
		  		   and goods.create_time = #{createTime} 
		  		   and goods.create_by = #{createBy} 
		  		   and goods.update_time = #{updateTime} 
		  		   and goods.update_by = #{updateBy} 
		  		   and goods.remarks = #{remarks} 




                order by ${sort} ${order}


                order by id desc



			limit #{offset}, #{limit}

		select count(*) from goods,product_product
        where goods.product_id=product_product.id and goods.deleteEnable=0

		  		   and id = #{id} 
		  		   and goods.name = #{name} 
		  		   and goods_number = #{goodsNumber} 
		  		   and tgoods_number = #{tgoodsNumber} 
		  		   and product_id = #{productId} 
		  		   and goods.status = #{status} 
		  		   and discount_price = #{discountPrice} 
		  		   and pay_price = #{payPrice} 
		  		   and vip_price = #{vipPrice} 
		  		   and promotion_price = #{promotionPrice} 
		  		   and create_time = #{createTime} 
		  		   and create_by = #{createBy} 
		  		   and update_time = #{updateTime} 
		  		   and update_by = #{updateBy} 
		  		   and remarks = #{remarks}
