mapper.select:
       select `id`,`open_id`,`nickname`,`password`,`phone`,`heard_url`,`name`,`identity_card`,`register_time`,`pay_num`,`serve_num`,`balance`,`restitution`,`pay_time`,`login_time`,`add_time`,`update_time`,`delete_flag`,`username`,`chengjiu_ioc`,`chengjiu_name`,`chengjiu_demand`,`reward`,`chengjiu_time`,`rank`,`chengjiu_flag`,`start_time`,`end_time`,`totalkou_num`,`daykou_num`,`sex`,`age`,`smoke_age`,`time` from t_user

		  		   and id = #{id} 
		  		   and open_id = #{openId} 
		  		   and nickname = #{nickname} 
		  		   and password = #{password} 
		  		   and phone = #{phone} 
		  		   and heard_url = #{heardUrl} 
		  		   and name = #{name} 
		  		   and identity_card = #{identityCard} 
		  		   and register_time = #{registerTime} 
		  		   and pay_num = #{payNum} 
		  		   and serve_num = #{serveNum} 
		  		   and balance = #{balance} 
		  		   and restitution = #{restitution} 
		  		   and pay_time = #{payTime} 
		  		   and login_time = #{loginTime} 
		  		   and add_time = #{addTime} 
		  		   and update_time = #{updateTime} 
		  		   and delete_flag = #{deleteFlag} 
		  		   and username = #{username} 
		  		   and chengjiu_ioc = #{chengjiuIoc} 
		  		   and chengjiu_name = #{chengjiuName} 
		  		   and chengjiu_demand = #{chengjiuDemand} 
		  		   and reward = #{reward} 
		  		   and chengjiu_time = #{chengjiuTime} 
		  		   and rank = #{rank} 
		  		   and chengjiu_flag = #{chengjiuFlag} 
		  		   and start_time = #{startTime} 
		  		   and end_time = #{endTime} 
		  		   and totalkou_num = #{totalkouNum} 
		  		   and daykou_num = #{daykouNum} 
		  		   and sex = #{sex} 
		  		   and age = #{age} 
		  		   and smoke_age = #{smokeAge} 
		  		   and time = #{time} 



                order by ${sort} ${order}


                order by id desc



			limit #{offset}, #{limit}

       select 
      IFNULL(`nickname`,'') as 用户昵称,
      IFNULL(`name`,'') as 用户姓名,
       IFNULL(`sex`,'') as 用户性别,
		IFNULL(`age`,'') as 用户年龄,
		IFNULL(`smoke_age`,'') as 用户烟龄,
      IFNULL(`password`,'') as 用户密码,
      IFNULL(`phone`,'') as 手机号,
     IFNULL(`identity_card`,'') as 身份证号,
     IFNULL(DATE_FORMAT(`register_time`,'%Y-%m-%d %h:%m:%s'),'') as 注册时间,
		IFNULL(DATE_FORMAT(`chengjiu_time`,'%Y-%m-%d %h:%m:%s'),'') as 成就解锁时间,
		IFNULL(`rank`,'') as 排名
		from t_user

		  		   and id = #{id} 
		  		   and open_id = #{openId} 
		  		   and nickname = #{nickname} 
		  		   and sex = #{sex} 
		  		   and age = #{age} 
		  		   and smoke_age = #{smokeAge} 
		  		   and password = #{password} 
		  		   and phone = #{phone} 
		  		   and heard_url = #{heardUrl} 
		  		   and name = #{name} 
		  		   and identity_card = #{identityCard} 
		  		   and register_time = #{registerTime} 
		  		   and pay_num = #{payNum} 
		  		   and serve_num = #{serveNum} 
		  		   and balance = #{balance} 
		  		   and restitution = #{restitution} 
		  		   and pay_time = #{payTime} 
		  		   and login_time = #{loginTime} 
		  		   and add_time = #{addTime} 
		  		   and update_time = #{updateTime} 
		  		   and delete_flag = #{deleteFlag} 
		  		   and username = #{username} 
		  		   and chengjiu_ioc = #{chengjiuIoc} 
		  		   and chengjiu_name = #{chengjiuName} 
		  		   and chengjiu_demand = #{chengjiuDemand} 
		  		   and reward = #{reward} 
		  		   and chengjiu_time = #{chengjiuTime} 
		  		   and rank = #{rank} 
		  		   and chengjiu_flag = #{chengjiuFlag} 
		  		   and start_time = #{startTime} 
		  		   and end_time = #{endTime} 
		  		   and totalkou_num = #{totalkouNum} 
		  		   and daykou_num = #{daykouNum} 		  		  
		  		   and time = #{time} 
		  		   and id in (${ids}) 



                order by ${sort} ${order}


                order by id desc



			limit #{offset}, #{limit}

